INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 1, 'ੴ', 'ik oNkaar', 'There is Only One God', 1);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 2, 'ਸਤਿ ਨਾਮੁ', 'Sat Naam', 'Truth is His Name', 1);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 3, 'ਕਰਤਾ ਪੁਰਖੁ ਨਿਰਭਉ ਨਿਰਵੈਰੁ ਅਕਾਲ ਮੂਰਤਿ', 'kartaa purakh nirbh-a-o nirvair akaal moorat', 'He is the Creator, Protector, Without Fear, No Enmity, The First Entity,', 1);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 4, 'ਅਜੂਨੀ ਸੈਭੰ ਗੁਰ ਪ੍ਰਸਾਦਿ.  || ਜਪੁ ||.', 'ajoonee saibhN gur parsaad. jap.', 'Never born, Self-perpetuating; With the Guru’s grace: Recite!', 1);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 5, 'ਆਦਿ ਸਚੁ ਜੁਗਾਦਿ ਸਚੁ.', 'aad sach jugaad sach.', 'True in the beginning, True Through the Ages,', 1);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 6, 'ਹੈ ਭੀ ਸਚੁ ਨਾਨਕ ਹੋਸੀ ਭੀ ਸਚੁ. 1.', 'hai bhee sach naanak hosee bhee sach. 1.', 'True even now and says Nanak will be True in the future 1.', 1);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 7, 'ਸੋਚੈ ਸੋਚਿ ਨ ਹੋਵਈ ਜੇ ਸੋਚੀ ਲਖ ਵਾਰ', 'sochai soch naa hov-a-ee jay sochee lakh vaar', 'By thinking and thinking again a hundred thousand times, one cannot find a solution', 2);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 8, 'ਚੁਪੈ ਚੁਪ ਨ ਹੋਵਈ ਜੇ ਲਾਇ ਰਹਾ ਲਿਵ ਤਾਰ', 'chupai chup naa hov-a-ee jay laa-i rahaa liv taar', 'By being quiet, peace cannot be found even if poised deep in meditation forever', 2);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 9, 'ਭੁਖਿਆ ਭੁਖ ਨ ਉਤਰੀ ਜੇ ਬੰਨਾ ਪੁਰੀਆ ਭਾਰ', 'bhukhi-aa bhukh naa utree jay bannaa puree-aa bhaar', 'The hunger of the hungry cannot be quenched if food is carried as baggage', 2);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 10, 'ਸਹਸ ਸਿਆਣਪਾ ਲਖ ਹੋਹਿ ਤ ਇਕ ਨ ਚਲੈ ਨਾਲਿ', 'sehas si-aanpaa lakh hoh taa ik naa chalai naal', 'One may possess a hundred thousand clever ideas, but even one will not accompany him/her', 2);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 11, 'ਕਿਵ ਸਚਿਆਰਾ ਹੋਈਐ ਕਿਵ ਕੂੜੈ ਤੁਟੈ ਪਾਲਿ', 'kiv sachi-aaraa ho-ee-ai kiv koorhai tutai paal', 'How then can one be purified? How can one throw away the falsehood?', 2);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 12, 'ਹੁਕਮਿ ਰਜਾਈ ਚਲਣਾ ਨਾਨਕ ਲਿਖਿਆ ਨਾਲਿ', 'hukam rajaa-ee chalnaa naanak likhi-aa naal', 'Says Nanak, By Abiding by the Command of God, which is written along with everyone', 2);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 13, 'ਹੁਕਮੀ ਹੋਵਨਿ ਆਕਾਰ ਹੁਕਮੁ ਨ ਕਹਿਆ ਜਾਈ', 'hukmee hovan aakaar hukam na kahi-aa jaa-ee.', 'By Your Command entities are formed. Your Command cannot be predicted.', 3);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 14, 'ਹੁਕਮੀ ਹੋਵਨਿ ਜੀਅ ਹੁਕਮਿ ਮਿਲੈ ਵਡਿਆਈ', 'hukmee hovan jee-a hukam milai vadi-aa-ee.', 'With your Command one obtains respect and is praised.', 3);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 15, 'ਹੁਕਮੀ ਉਤਮੁ ਨੀਚੁ ਹੁਕਮਿ ਲਿਖਿ ਦੁਖ ਸੁਖ ਪਾਈਅਹਿ', 'hukmee utam neech hukam likh dukh sukh paa-ee-ah.', 'By Your Command Ups & Downs occur. With Your Command is written suffering and happiness.', 3);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 16, 'ਇਕਨਾ ਹੁਕਮੀ ਬਖਸੀਸ ਇਕਿ ਹੁਕਮੀ ਸਦਾ ਭਵਾਈਅਹਿ', 'iknaa hukmee bakhsees ik hukmee sadaa bhavaa-ee-ah.', 'Without Your Command no one is blessed. With your Command one lives in perpetual migrations.', 3);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 17, 'ਹੁਕਮੈ ਅੰਦਰਿ ਸਭੁ ਕੋ ਬਾਹਰਿ ਹੁਕਮ ਨ ਕੋਇ', 'hukmai andar sabh ko baahar hukam na ko-ay.', 'Everyone in Within Your Command, None is outside His Order.', 3);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 18, 'ਨਾਨਕ ਹੁਕਮੈ ਜੇ ਬੁਝੈ ਤ ਹਉਮੈ ਕਹੈ ਨ ਕੋਇ', 'naanak hukmai jay bujhai ta ha-umai kahai na ko-ay.', 'Says Nanak, \"Whosoever realizes Your Command, Never speaks in ego.\"', 3);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 19, 'ਗਾਵੈ ਕੋ ਤਾਣੁ ਹੋਵੈ ਕਿਸੈ ਤਾਣੁ ਗਾਵੈ ਕੋ ਦਾਤਿ ਜਾਣੈ ਨੀਸਾਣੁ', 'gaavai ko taan hovai kisai taan. gaavai ko daat jaanai neesaan.', 'Many sing of His power, who has that power. Many sing of the Gift, who knows the sign.', 4);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 20, 'ਗਾਵੈ ਕੋ ਗੁਣ ਵਡਿਆਈਆ ਚਾਰ ਗਾਵੈ ਕੋ ਵਿਦਿਆ ਵਿਖਮੁ ਵੀਚਾਰੁ', 'gaavai ko gun vadi-aa-ee-aa chaar. gaavai ko vidi-aa vikham veechaar.', 'Many sing of virtues, praises and greatness. Many sing of knowledge, wisdom and understanding.', 4);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 21, 'ਗਾਵੈ ਕੋ ਸਾਜਿ ਕਰੇ ਤਨੁ ਖੇਹ ਗਾਵੈ ਕੋ ਜੀਅ ਲੈ ਫਿਰਿ ਦੇਹ', 'gaavai ko saaj karay tan khayh. gaavai ko jee-a lai fir dayh.', 'Many sing about how He creates and destroys Many sing about how He takes life and then restores it.', 4);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 22, 'ਗਾਵੈ ਕੋ ਜਾਪੈ ਦਿਸੈ ਦੂਰਿ ਗਾਵੈ ਕੋ ਵੇਖੈ ਹਾਦਰਾ ਹਦੂਰਿ', 'gaavai ko jaapai disai door. gaavai ko vaykhai haadraa hadoor.', 'Many sing, chant and see far Many sing that He watches, is ever-present one-on-one.', 4);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 23, 'ਕਥਨਾ ਕਥੀ ਨ ਆਵੈ ਤੋਟਿ ਕਥਿ ਕਥਿ ਕਥੀ ਕੋਟੀ ਕੋਟਿ ਕੋਟਿ', 'kathnaa kathee na aavai tot.	kath kath kathee kotee kot kot.', 'There is no shortage of those who preach and teach. Millions upon millions offer millions of sermons and stories.', 4);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 24, 'ਦੇਦਾ ਦੇ ਲੈਦੇ ਥਕਿ ਪਾਹਿ ਜੁਗਾ ਜੁਗੰਤਰਿ ਖਾਹੀ ਖਾਹਿ', 'daydaa day laiday thak paahi. jugaa jugantar khaahee khaahi.', 'The Great Giver keeps on giving, the receivers grow weary of receiving. Throughout the ages, the consumers keep on consuming.', 4);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 25, 'ਹੁਕਮੀ ਹੁਕਮੁ ਚਲਾਏ ਰਾਹੁ ਨਾਨਕ ਵਿਗਸੈ ਵੇਪਰਵਾਹੁ', 'hukmee hukam chalaa-ay raahu. naanak vigsai vayparvaahu.', 'The Commander, by His Command, leads us to walk on the Path. O Nanak, only He blossoms forth, carefree and untroubled.', 4);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 26, 'ਸਾਚਾ ਸਾਹਿਬੁ ਸਾਚੁ ਨਾਇ ਭਾਖਿਆ ਭਾਉ ਅਪਾਰੁ', 'saachaa saahib saach naa-ay bhaakhi-aa bhaa-o apaar.', 'True Master, True is His Name - Meditate it with utmost humility and deep love.', 5);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 27, 'ਆਖਹਿ ਮੰਗਹਿ ਦੇਹਿ ਦੇਹਿ ਦਾਤਿ ਕਰੇ ਦਾਤਾਰੁ', 'aakhahi mangahi dayhi dayhi daat karay daataar.', 'Ask and plead, \"Give, give\" - The Giver gives in abundance.', 5);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 28, 'ਫੇਰਿ ਕਿ ਅਗੈ ਰਖੀਐ ਜਿਤੁ ਦਿਸੈ ਦਰਬਾਰੁ', 'fayr ke agai rakhee-ai jit disai darbaar.', 'What can we place before Him, by which the Darbaar can be seen?', 5);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 29, 'ਮੁਹੌ ਕਿ ਬੋਲਣੁ ਬੋਲੀਐ ਜਿਤੁ ਸੁਣਿ ਧਰੇ ਪਿਆਰੁ', 'muhou ke bolan bolee-ai jit sun Dharay pi-aar.', 'What words can our mouth utter, listening to which His Love is gained?', 5);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 30, 'ਅੰਮ੍ਰਿਤ ਵੇਲਾ ਸਚੁ ਨਾਉ ਵਡਿਆਈ ਵੀਚਾਰੁ', 'amrit vaylaa sach naa-o vadi-aa-ee veechaar.', 'In the Ambrosial period, dwell upon His True Name and praises.', 5);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 31, 'ਕਰਮੀ ਆਵੈ ਕਪੜਾ ਨਦਰੀ ਮੋਖੁ ਦੁਆਰੁ', 'karmee aavai kaprhaa nadree mokh du-aar.', 'With good past karmas, this robe is obtained, By His Grace, the Gate of Liberation is found.', 5);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 32, 'ਨਾਨਕ ਏਵੈ ਜਾਣੀਐ ਸਭੁ ਆਪੇ ਸਚਿਆਰੁ', 'naanak evai jaanee-ai sabh aapay sachiaar.', 'O Nanak, know this well: Everything is in the Righteous Lord!', 5);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 33, 'ਥਾਪਿਆ ਨ ਜਾਇ ਕੀਤਾ ਨ ਹੋਇ ਆਪੇ ਆਪਿ ਨਿਰੰਜਨੁ ਸੋਇ', 'thaapi-aa na jaa-ay keetaa na ho-ay. aapay aap niranjan so-ay.', 'He cannot be established; By doing it cannot be done. He Himself is Immaculate and Pure.', 6);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 34, 'ਜਿਨਿ ਸੇਵਿਆ ਤਿਨਿ ਪਾਇਆ ਮਾਨੁ ਨਾਨਕ ਗਾਵੀਐ ਗੁਣੀ ਨਿਧਾਨੁ', 'jin sayvi-aa tin paa-i-aa maan. naanak gaavee-ai gunee niDhaan.', 'Whoever serves Him receives honor. O Nanak sing - \"Only Lord has endless Virtues\".', 6);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 35, 'ਗਾਵੀਐ ਸੁਣੀਐ ਮਨਿ ਰਖੀਐ ਭਾਉ ਦੁਖੁ ਪਰਹਰਿ ਸੁਖੁ ਘਰਿ ਲੈ ਜਾਇ', 'gaavee-ai sunee-ai man rakhee-ai bhaa-o. dukh parhar sukh ghar lai jaa-ay.', 'Sing, listen, and keep your mind humble. For your pains, take home every peace.', 6);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 36, 'ਗੁਰਮੁਖਿ ਨਾਦੰ ਗੁਰਮੁਖਿ ਵੇਦੰ ਗੁਰਮੁਖਿ ਰਹਿਆ ਸਮਾਈ', 'gurmukh naadaN gurmukh vaydaN gurmukh rahi-aa samaa-ee.', 'The Guru’s Word is the Sound-current, wisdom of the Vedas and remains the ultimate protector.', 6);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 37, 'ਗੁਰੁ ਈਸਰੁ ਗੁਰੁ ਗੋਰਖੁ ਬਰਮਾ ਗੁਰੁ ਪਾਰਬਤੀ ਮਾਈ', 'gur eesar gur gorakh barmaa gur paarbatee maa-ee.', 'The Guru is Shiva, Gorak and Brahma; the Guru is Paarvati and Lakhshmi.', 6);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 38, 'ਜੇ ਹਉ ਜਾਣਾ ਆਖਾ ਨਾਹੀ ਕਹਣਾ ਕਥਨੁ ਨ ਜਾਈ', 'jay ha-o jaanaa aakhaa naahee kahnaa kathan na jaa-ee.', 'Even knowing Him, I cannot describe Him; He is beyond description in words.', 6);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 39, 'ਗੁਰਾ ਇਕ ਦੇਹਿ ਬੁਝਾਈ ਸਭਨਾ ਜੀਆ ਕਾ ਇਕੁ ਦਾਤਾ ਸੋ ਮੈ ਵਿਸਰਿ ਨ ਜਾਈ', 'guraa ik dayhi bujhaa-ee. sabhnaa jee-aa kaa ik daataa so mai visar na jaa-ee. 5', 'The Guru has extinguished this doubt:All creatures have only the one God – May I never forget this! 5', 6);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 40, 'ਤੀਰਥਿ ਨਾਵਾ ਜੇ ਤਿਸੁ ਭਾਵਾ ਵਿਣੁ ਭਾਣੇ ਕਿ ਨਾਇ ਕਰੀ', 'tirath naavaa jay tis bhaavaa vin bhaanay ke naa-ay karee.', 'If by bathing at holy places, He could be obtained, Without His Blessing one cannot get there', 7);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 41, 'ਜੇਤੀ ਸਿਰਠਿ ਉਪਾਈ ਵੇਖਾ ਵਿਣੁ ਕਰਮਾ ਕਿ ਮਿਲੈ ਲਈ', 'jaytee sirath upaa-ee vaykhaa vin karmaa ke milai la-ee.', 'I gaze upon His Creation: without good karmas, what will they receive?', 7);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 42, 'ਮਤਿ ਵਿਚਿ ਰਤਨ ਜਵਾਹਰ ਮਾਣਿਕ ਜੇ ਇਕ ਗੁਰ ਕੀ ਸਿਖ ਸੁਣੀ', 'mat vich ratan javaahar maanik jay ik gur kee sikh sunee.', 'Within the mind are gems, jewels and rubies, when the Sikh listens to the One Guru.', 7);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 43, 'ਗੁਰਾ ਇਕ ਦੇਹਿ ਬੁਝਾਈ', 'guraa ik dayhi bujhaa-ee.', 'The Guru has extinguished this doubt:', 7);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 44, 'ਸਭਨਾ ਜੀਆ ਕਾ ਇਕੁ ਦਾਤਾ ਸੋ ਮੈ ਵਿਸਰਿ ਨ ਜਾਈ', 'sabhnaa jee-aa kaa ik daataa so mai visar na jaa-ee.', 'All creatures have only the one God – May I never forget this! 6', 7);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 45, 'ਜੇ ਜੁਗ ਚਾਰੇ ਆਰਜਾ ਹੋਰ ਦਸੂਣੀ ਹੋਇ', 'jay jug chaaray aarjaa hor dasoonee ho-ay.', 'Even if you lived the four ages, or even ten times more,', 8);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 46, 'ਨਵਾ ਖੰਡਾ ਵਿਚਿ ਜਾਣੀਐ ਨਾਲਿ ਚਲੈ ਸਭੁ ਕੋਇ', 'navaa khanda vich jaanee-ai naal chalai sabh ko-ay.', 'and even if you were known throughout the nine continents and everyone followed you,', 8);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 47, 'ਚੰਗਾ ਨਾਉ ਰਖਾਇ ਕੈ ਜਸੁ ਕੀਰਤਿ ਜਗਿ ਲੇਇ', 'changa naa-o rakhaa-ay kai jas keerat jag lay-ay.', 'with a good name and reputation, the whole world sang your praises –', 8);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 48, 'ਜੇ ਤਿਸੁ ਨਦਰਿ ਨ ਆਵਈ ਤ ਵਾਤ ਨ ਪੁਛੈ ਕੇ', 'jay tis nadar na aavee ta vaat na puchhai kay.', 'still, if you do not please the Lord, no one will ask about you!', 8);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 49, 'ਕੀਟਾ ਅੰਦਰਿ ਕੀਟੁ ਕਰਿ ਦੋਸੀ ਦੋਸੁ ਧਰੇ', 'keetaa andar keet kar dosee dos Dharay.', 'Among worms, you would be considered a lowly worm, and even contemptible sinners would hold you in contempt.', 8);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 50, 'ਨਾਨਕ ਨਿਰਗੁਣਿ ਗੁਣੁ ਕਰੇ ਗੁਣਵੰਤਿਆ ਗੁਣੁ ਦੇ', 'naanak nirgun gun karay gunvanti-aa gun day.', 'O Nanak, He makes unworthy, worthy and gives virtues to the virtuous.', 8);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 51, 'ਤੇਹਾ ਕੋਇ ਨ ਸੁਝਈ ਜਿ ਤਿਸੁ ਗੁਣੁ ਕੋਇ ਕਰੇ', 'tayhaa ko-ay na sujh-ee je tis gun ko-ay karay. 7', 'I know of no other who can grant so much to others! 7', 8);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 52, 'ਸੁਣਿਐ ਸਿਧ ਪੀਰ ਸੁਰਿ ਨਾਥ', 'suni-ai siDh peer sur naath.', 'Listening - the saints, priests, warriors and recluse.', 9);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 53, 'ਸੁਣਿਐ ਧਰਤਿ ਧਵਲ ਆਕਾਸ', 'suni-ai Dharat Dhaval aakaas.', 'Listening - the earth, its support and sky.', 9);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 54, 'ਸੁਣਿਐ ਦੀਪ ਲੋਅ ਪਾਤਾਲ', 'suni-ai deep lo-a paataal.', 'Listening - the oceans, lands of the world and the nether regions.', 9);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 55, 'ਸੁਣਿਐ ਪੋਹਿ ਨ ਸਕੈ ਕਾਲੁ', 'suni-ai pohi na sakai kaal.', 'Listening - Death cannot even touch you', 9);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 56, 'ਨਾਨਕ ਭਗਤਾ ਸਦਾ ਵਿਗਾਸੁ', 'naanak bhagtaa sadaa vigaas.', 'O Nanak, the devotees are forever in bliss.', 9);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 57, 'ਸੁਣਿਐ ਦੂਖ ਪਾਪ ਕਾ ਨਾਸੁ', 'suni-ai dookh paap kaa naas.', 'Listening - pain and sin are erased. 8', 9);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 58, 'ਸੁਣਿਐ ਈਸਰੁ ਬਰਮਾ ਇੰਦੁ', 'suni-ai eesar barmaa ind.', 'Listening - Shiva, Brahma and Indra.', 10);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 59, 'ਸੁਣਿਐ ਮੁਖਿ ਸਾਲਾਹਣ ਮੰਦੁ', 'suni-ai mukh saalaahan mand.', 'Listening - even foul-mouthed people praise Him.', 10);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 60, 'ਸੁਣਿਐ ਜੋਗ ਜੁਗਤਿ ਤਨਿ ਭੇਦ', 'suni-ai jog jugat tan bhayd.', 'Listening - the technology of Yoga and the secrets of the body.', 10);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 61, 'ਸੁਣਿਐ ਸਾਸਤ ਸਿਮ੍ਰਿਤਿ ਵੇਦ', 'suni-ai saasat simrit vayd.', 'Listening – the Holy Books, Philosophy and the Vedas.', 10);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 62, 'ਨਾਨਕ ਭਗਤਾ ਸਦਾ ਵਿਗਾਸੁ', 'naanak bhagtaa sadaa vigaas.', 'O Nanak, the devotees are forever in bliss.', 10);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 63, 'ਸੁਣਿਐ ਦੂਖ ਪਾਪ ਕਾ ਨਾਸੁ', 'suni-ai dookh paap kaa naas. 9', 'Listening-pain and sin are erased. 9', 10);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 64, 'ਸੁਣਿਐ ਸਤੁ ਸੰਤੋਖੁ ਗਿਆਨੁ', 'suni-ai sat santokh gi-aan.', 'Listening - truth, contentment and spiritual wisdom.', 11);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 65, 'ਸੁਣਿਐ ਅਠਸਠਿ ਕਾ ਇਸਨਾਨੁ', 'suni-ai athsath kaa isnaan.', 'Listening - bathe at the sixty-eight holy places.', 11);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 66, 'ਸੁਣਿਐ ਪੜਿ ਪੜਿ ਪਾਵਹਿ ਮਾਨੁ', 'suni-ai parh parh paavahi maan.', 'Listening - reading and reciting, honor is obtained.', 11);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 67, 'ਸੁਣਿਐ ਲਾਗੈ ਸਹਜਿ ਧਿਆਨੁ', 'suni-ai laagai sahj Dhi-aan.', 'Listening – one attains sure dedication.', 11);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 68, 'ਨਾਨਕ ਭਗਤਾ ਸਦਾ ਵਿਗਾਸੁ', 'naanak bhagtaa sadaa vigaas.', 'O Nanak, the devotees are forever in bliss.', 11);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 69, 'ਸੁਣਿਐ ਦੂਖ ਪਾਪ ਕਾ ਨਾਸੁ', 'suni-ai dookh paap kaa naas. 10', 'Listening-pain and sin are erased. 10', 11);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 70, 'ਸੁਣਿਐ ਸਰਾ ਗੁਣਾ ਕੇ ਗਾਹ', 'suni-ai saraa gunaa kay gaah.', 'Listening - dive deep into the ocean of virtue.', 12);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 71, 'ਸੁਣਿਐ ਸੇਖ ਪੀਰ ਪਾਤਿਸਾਹ', 'suni-ai saykh peer paatisaah.', 'Listening - the religious scholars, spiritual teachers and emperors.', 12);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 72, 'ਸੁਣਿਐ ਅੰਧੇ ਪਾਵਹਿ ਰਾਹੁ', 'suni-ai anDhay paavahi raahu.', 'Listening - even the blind find the Path.', 12);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 73, 'ਸੁਣਿਐ ਹਾਥ ਹੋਵੈ ਅਸਗਾਹੁ', 'suni-ai haath hovai asgaahu.', 'Listening - the Unreachable comes within grasp.', 12);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 74, 'ਨਾਨਕ ਭਗਤਾ ਸਦਾ ਵਿਗਾਸੁ', 'naanak bhagtaa sadaa vigaas.', 'O Nanak, the devotees are forever in bliss.', 12);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 75, 'ਸੁਣਿਐ ਦੂਖ ਪਾਪ ਕਾ ਨਾਸੁ', 'suni-ai dookh paap kaa naas. 11', 'Listening-pain and sin are erased. 11', 12);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 76, 'ਮੰਨੇ ਕੀ ਗਤਿ ਕਹੀ ਨ ਜਾਇ', 'mannay kee gat kahee na jaa-ay.', 'The state of the Believer cannot be described.', 13);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 77, 'ਜੇ ਕੋ ਕਹੈ ਪਿਛੈ ਪਛੁਤਾਇ', 'jay ko kahai pichhai pachhutaa-ay.', 'The One who tries shall regret the attempt.', 13);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 78, 'ਕਾਗਦਿ ਕਲਮ ਨ ਲਿਖਣਹਾਰੁ', 'kaagad kalam na likhanhaar.', 'Neither paper nor pen can narrate.', 13);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 79, 'ਮੰਨੇ ਕਾ ਬਹਿ ਕਰਨਿ ਵੀਚਾਰੁ', 'mannay kaa bahi karan veechaar.', 'Many study the concept of faith.', 13);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 80, 'ਐਸਾ ਨਾਮੁ ਨਿਰੰਜਨੁ ਹੋਇ', 'aisaa naam niranjan ho-ay.', 'Such personality obtains purity.', 13);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 81, 'ਜੇ ਕੋ ਮੰਨਿ ਜਾਣੈ ਮਨਿ ਕੋਇ', 'jay ko man jaanai man ko-ay. 12', 'If one mind understands another’s mind. 12', 13);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 82, 'ਮੰਨੈ ਸੁਰਤਿ ਹੋਵੈ ਮਨਿ ਬੁਧਿ', 'mannai surat hovai man buDh.', 'With faith, awareness and enlightenment is obtained.', 14);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 83, 'ਮੰਨੈ ਸਗਲ ਭਵਣ ਕੀ ਸੁਧਿ', 'mannai sagal bhavan kee suDh.', 'With faith, all types of realms are understood.', 14);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 84, 'ਮੰਨੈ ਮੁਹਿ ਚੋਟਾ ਨਾ ਖਾਇ', 'mannai muhi chotaa naa khaa-ay.', 'The faithful do not face trouble.', 14);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 85, 'ਮੰਨੈ ਜਮ ਕੈ ਸਾਥਿ ਨ ਜਾਇ', 'mannai jam kai saath na jaa-ay.', 'The faithful do not go with the Messenger of Death.', 14);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 86, 'ਐਸਾ ਨਾਮੁ ਨਿਰੰਜਨੁ ਹੋਇ', 'aisaa naam niranjan ho-ay.', 'Such personality obtains purity.', 14);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 87, 'ਜੇ ਕੋ ਮੰਨਿ ਜਾਣੈ ਮਨਿ ਕੋਇ', 'jay ko man jaanai man ko-ay. 13', 'If one mind understands another’s mind. 13', 14);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 88, 'ਮੰਨੈ ਮਾਰਗਿ ਠਾਕ ਨ ਪਾਇ', 'mannai maarag thaak na paa-ay.', 'The path of the faithful is never blocked.', 15);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 89, 'ਮੰਨੈ ਪਤਿ ਸਿਉ ਪਰਗਟੁ ਜਾਇ', 'mannai pat si-o pargat jaa-ay.', 'The faithful shall depart with honor and fame.', 15);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 90, 'ਮੰਨੈ ਮਗੁ ਨ ਚਲੈ ਪੰਥੁ', 'mannai mag na chalai panth.', 'The faithful do not follow empty religious rituals.', 15);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 91, 'ਮੰਨੈ ਧਰਮ ਸੇਤੀ ਸਨਬੰਧੁ', 'mannai Dharam saytee san-banDh.', 'The faithful are firmly bound to the Dharma.', 15);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 92, 'ਐਸਾ ਨਾਮੁ ਨਿਰੰਜਨੁ ਹੋਇ', 'aisaa naam niranjan ho-ay.', 'Such personality obtains purity.', 15);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 93, 'ਜੇ ਕੋ ਮੰਨਿ ਜਾਣੈ ਮਨਿ ਕੋਇ', 'jay ko man jaanai man ko-ay. 14', 'If one mind understands another’s mind.14', 15);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 94, 'ਮੰਨੈ ਪਾਵਹਿ ਮੋਖੁ ਦੁਆਰੁ', 'mannai paavahi mokh du-aar.', 'The faithful find the Door of Liberation.', 16);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 95, 'ਮੰਨੈ ਪਰਵਾਰੈ ਸਾਧਾਰੁ', 'mannai parvaarai saaDhaar.', 'The faithful uplift and redeem their family and relations.', 16);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 96, 'ਮੰਨੈ ਤਰੈ ਤਾਰੇ ਗੁਰੁ ਸਿਖ', 'mannai tarai taaray gur sikh.', 'The faithful are saved, together with the Sikhs of the Guru.', 16);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 97, 'ਮੰਨੈ ਨਾਨਕ ਭਵਹਿ ਨ ਭਿਖ', 'mannai naanak bhavahi na bhikh.', 'The faithful, O Nanak, do not wander around begging.', 16);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 98, 'ਐਸਾ ਨਾਮੁ ਨਿਰੰਜਨੁ ਹੋਇ', 'aisaa naam niranjan ho-ay.', 'Such personality obtains purity.', 16);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 99, 'ਜੇ ਕੋ ਮੰਨਿ ਜਾਣੈ ਮਨਿ ਕੋਇ', 'jay ko man jaanai man ko-ay. 15', 'If one mind understands another’s mind. 15', 16);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 100, 'ਪੰਚ ਪਰਵਾਣ ਪੰਚ ਪਰਧਾਨੁ ਪੰਚੇ ਪਾਵਹਿ ਦਰਗਹਿ ਮਾਨੁ', 'panch parvaan panch parDhaan. panchay paavahi dargahi maan.', 'The chosen are accepted and preside. The chosen are honored in the Court of the Lord.', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 101, 'ਪੰਚੇ ਸੋਹਹਿ ਦਰਿ ਰਾਜਾਨੁ ਪੰਚਾ ਕਾ ਗੁਰੁ ਏਕੁ ਧਿਆਨੁ', 'panchay sohahi dar raajaan. panchaa kaa gur ayk Dhi-aan.', 'The chosen look beautiful in the courts of kings. The chosen meditate single-mindedly on the Guru.', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 102, 'ਜੇ ਕੋ ਕਹੈ ਕਰੈ ਵੀਚਾਰੁ ਕਰਤੇ ਕੈ ਕਰਣੈ ਨਾਹੀ ਸੁਮਾਰੁ', 'jay ko kahai karai veechaar. kartay kai karnai naahee sumaar.', 'What is said, mediate on this thought. the actions of the Creator cannot be counted.', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 103, 'ਧੌਲੁ ਧਰਮੁ ਦਇਆ ਕਾ ਪੂਤੁ ਸੰਤੋਖੁ ਥਾਪਿ ਰਖਿਆ ਜਿਨਿ ਸੂਤਿ', 'Dhoul Dharam da-i-aa kaa poot. santokh thaap rakhi-aa jin soot.', 'Bull, the Dharma, is the son of compassion; Contentment patiently holds it in its place.', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 104, 'ਜੇ ਕੋ ਬੁਝੈ ਹੋਵੈ ਸਚਿਆਰੁ ਧਵਲੈ ਉਪਰਿ ਕੇਤਾ ਭਾਰੁ', 'jay ko bujhai hovai sachiaar. Dhavlai upar kaytaa bhaar.', 'One who understands becomes pure. What is the great load on the bull?', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 105, 'ਧਰਤੀ ਹੋਰੁ ਪਰੈ ਹੋਰੁ ਹੋਰੁ ਤਿਸ ਤੇ ਭਾਰੁ ਤਲੈ ਕਵਣੁ ਜੋਰੁ', 'Dhartee hor parai hor hor. tis tay bhaar talai kavan jor', 'There is another world, then many, more and more! Who holds their weight in balance with this force?', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 106, 'ਜੀਅ ਜਾਤਿ ਰੰਗਾ ਕੇ ਨਾਵ ਸਭਨਾ ਲਿਖਿਆ ਵੁੜੀ ਕਲਾਮ', 'jee-a jaat rangaa kay naav. sabhnaa likhi-aa vurhee kalaam.', 'Beings, species, colors have names. were all inscribed by the Almighty’s Pen', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 107, 'ਏਹੁ ਲੇਖਾ ਲਿਖਿ ਜਾਣੈ ਕੋਇ ਲੇਖਾ ਲਿਖਿਆ ਕੇਤਾ ਹੋਇ', 'ayhu laykhaa likh jaanai ko-ay. laykhaa likhi-aa kaytaa ho-ay.', 'This written account is only known to a few? How massive is this written account?', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 108, 'ਕੇਤਾ ਤਾਣੁ ਸੁਆਲਿਹੁ ਰੂਪੁ ਕੇਤੀ ਦਾਤਿ ਜਾਣੈ ਕੌਣੁ ਕੂਤੁ', 'kaytaa taan su-aalihu roop. kaytee daat jaanai koun koot.', 'What awesome power, fascinating beauty! Whose wonderful gifts! Who can know their limit?', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 109, 'ਕੀਤਾ ਪਸਾਉ ਏਕੋ ਕਵਾਉ ਤਿਸ ਤੇ ਹੋਏ ਲਖ ਦਰੀਆਉ', 'keetaa pasaa-o ayko kavaa-o. tis tay ho-ay lakh daree-aa-o.', 'He created the Universe with One Word!	Within it flow hundreds of thousands of rivers.', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 110, 'ਕੁਦਰਤਿ ਕਵਣ ਕਹਾ ਵੀਚਾਰੁ ਵਾਰਿਆ ਨ ਜਾਵਾ ਏਕ ਵਾਰ', 'kudrat kavan kahaa veechaar. vaari-aa na jaavaa ayk vaar.', 'Think about the mighty Creative Potency? I cannot even begin to describe You.', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 111, 'ਜੋ ਤੁਧੁ ਭਾਵੈ ਸਾਈ ਭਲੀ ਕਾਰ ਤੂ ਸਦਾ ਸਲਾਮਤਿ ਨਿਰੰਕਾਰ', 'jo tuDh bhaavai saa-ee bhalee kaar. too sadaa salaamat nirankaar.16', 'Whatever pleases You, All will be blessed, You always protect us, Formless One! 16', 17);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 112, 'ਅਸੰਖ ਜਪ ਅਸੰਖ ਭਾਉ ਅਸੰਖ ਪੂਜਾ ਅਸੰਖ ਤਪ ਤਾਉ', 'asaNkh jap asaNkh bhaa-o. asaNkh poojaa asaNkh tap taa-o.', 'Countless mediate, countless yearn. Countless worship, countless exercise self-restrain.', 18);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 113, 'ਅਸੰਖ ਗਰੰਥ ਮੁਖਿ ਵੇਦ ਪਾਠ ਅਸੰਖ ਜੋਗ ਮਨਿ ਰਹਹਿ ਉਦਾਸ', 'asaNkh garanth mukh vayd paath. asaNkh jog man rahahi udaas.', 'Countless recite granths and Vedas prayers. Countless Yogis detached their minds from the world.', 18);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 114, 'ਅਸੰਖ ਭਗਤ ਗੁਣ ਗਿਆਨ ਵੀਚਾਰ ਅਸੰਖ ਸਤੀ ਅਸੰਖ ਦਾਤਾਰ', 'asaNkh bhagat gun gi-aan veechaar. asaNkh satee asaNkh daataar.', 'Countless devotees contemplate the Virtues and Wisdoms. Countless sacrifice; countless donate.', 18);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 115, 'ਅਸੰਖ ਸੂਰ ਮੁਹ ਭਖ ਸਾਰ ਅਸੰਖ ਮੋਨਿ ਲਿਵ ਲਾਇ ਤਾਰ', 'asaNkh soor muh bhakh saar. asaNkh mon liv laa-ay taar.', 'Countless warriors, face attack head on. Countless in silence, focus their thoughts.', 18);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 116, 'ਕੁਦਰਤਿ ਕਵਣ ਕਹਾ ਵੀਚਾਰੁ ਵਾਰਿਆ ਨ ਜਾਵਾ ਏਕ ਵਾਰ', 'kudrat kavan kahaa veechaar. vaari-aa na jaavaa ayk vaar.', 'Think about the mighty Creative Potency? I cannot even begin to describe You.', 18);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 117, 'ਜੋ ਤੁਧੁ ਭਾਵੈ ਸਾਈ ਭਲੀ ਕਾਰ ਤੂ ਸਦਾ ਸਲਾਮਤਿ ਨਿਰੰਕਾਰ', 'jo tuDh bhaavai saa-ee bhalee kaar. too sadaa salaamat nirankaar. 17', 'Whatever pleases You, All will be blessed, You always protect us, Formless One! 17', 18);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 118, 'ਅਸੰਖ ਮੂਰਖ ਅੰਧ ਘੋਰ ਅਸੰਖ ਚੋਰ ਹਰਾਮਖੋਰ', 'asaNkh moorakh anDh ghor. asaNkh chor haraamkhor.', 'Countless fools, blinded by ignorance. Countless thieves and cheaters.', 19);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 119, 'ਅਸੰਖ ਅਮਰ ਕਰਿ ਜਾਹਿ ਜੋਰ ਅਸੰਖ ਗਲਵਢ ਹਤਿਆ ਕਮਾਹਿ', 'asaNkh amar kar jaahi jor. asaNkh galvadh hati-aa kamaahi.', 'Countless impose their will by force. Countless cut-throats and gather sins.', 19);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 120, 'ਅਸੰਖ ਪਾਪੀ ਪਾਪੁ ਕਰਿ ਜਾਹਿ ਅਸੰਖ ਕੂੜਿਆਰ ਕੂੜੇ ਫਿਰਾਹਿ', 'asaNkh paapee paap kar jaahi. asaNkh koorhi-aar koorhay firaahi.', 'Countless sinners who keep on sinning. Countless liars, wander lost in their lies.', 19);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 121, 'ਅਸੰਖ ਮਲੇਛ ਮਲੁ ਭਖਿ ਖਾਹਿ ਅਸੰਖ ਨਿੰਦਕ ਸਿਰਿ ਕਰਹਿ ਭਾਰੁ', 'asaNkh malaychh mal bhakh khaahi. asaNkh nindak sir karahi bhaar.', 'Countless wretches, eat filth for food. Countless slanders, make their heads heavy', 19);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 122, 'ਨਾਨਕੁ ਨੀਚੁ ਕਹੈ ਵੀਚਾਰੁ ਵਾਰਿਆ ਨ ਜਾਵਾ ਏਕ ਵਾਰ', 'naanak neech kahai veechaar. vaari-aa na jaavaa ayk vaar.', 'Lowly Nanak, gives this explanation. I cannot even begin to describe You.', 19);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 123, 'ਜੋ ਤੁਧੁ ਭਾਵੈ ਸਾਈ ਭਲੀ ਕਾਰ ਤੂ ਸਦਾ ਸਲਾਮਤਿ ਨਿਰੰਕਾਰ', 'jo tuDh bhaavai saa-ee bhalee kaar. too sadaa salaamat nirankaar. 18', 'Whatever pleases You, All will be blessed, You always protect us, Formless One! 18', 19);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 124, 'ਅਸੰਖ ਨਾਵ ਅਸੰਖ ਥਾਵ ਅਗੰਮ ਅਗੰਮ ਅਸੰਖ ਲੋਅ', 'asaNkh naav asaNkh thaav. agamm agamm asaNkh lo-a.', 'Countless names, countless places.	Inaccessible, unapproachable, countless celestial bodies.', 20);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 125, 'ਅਸੰਖ ਕਹਹਿ ਸਿਰਿ ਭਾਰੁ ਹੋਇ ਅਖਰੀ ਨਾਮੁ ਅਖਰੀ ਸਾਲਾਹ', 'asaNkh kehahi sir bhaar ho-ay. akhree naam akhree saalaah.', 'Even to call them countless is to carry weight on your head. The Word brings Naam; the Word brings Praise.', 20);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 126, 'ਅਖਰੀ ਗਿਆਨੁ ਗੀਤ ਗੁਣ ਗਾਹ ਅਖਰੀ ਲਿਖਣੁ ਬੋਲਣੁ ਬਾਣਿ', 'akhree gi-aan geet gun gaah. akhree likhan bolan baan.', 'The Word brings wisdom and the singing of songs of His Glory. The Word brings the written and spoken words and hymns.', 20);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 127, 'ਅਖਰਾ ਸਿਰਿ ਸੰਜੋਗੁ ਵਖਾਣਿ ਜਿਨਿ ਏਹਿ ਲਿਖੇ ਤਿਸੁ ਸਿਰਿ ਨਾਹਿ', 'akhraa sir sanjog vakhaan. jin ayhi likhay tis sir naahi.', 'The Word brings destiny written on one''s forehead. But Writer of the destiny – None are written on His Forehead.', 20);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 128, 'ਜਿਵ ਫੁਰਮਾਏ ਤਿਵ ਤਿਵ ਪਾਹਿ ਜੇਤਾ ਕੀਤਾ ਤੇਤਾ ਨਾਉ ਵਿਣੁ ਨਾਵੈ ਨਾਹੀ ਕੋ ਥਾਉ', 'jiv furmaa-ay tiv tiv paahi. jaytaa keetaa taytaa naa-o. vin naavai naahee ko thaa-o.', 'As He proclaims, so do we receive.Whatever You do, its in Your Name. Without Your Name, there is no place at all.', 20);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 129, 'ਕੁਦਰਤਿ ਕਵਣ ਕਹਾ ਵੀਚਾਰੁ ਵਾਰਿਆ ਨ ਜਾਵਾ ਏਕ ਵਾਰ', 'kudrat kavan kahaa veechaar. vaari-aa na jaavaa ayk vaar.', 'Think about the mighty Creative Potency? I cannot even begin to describe You.', 20);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 130, 'ਜੋ ਤੁਧੁ ਭਾਵੈ ਸਾਈ ਭਲੀ ਕਾਰ ਤੂ ਸਦਾ ਸਲਾਮਤਿ ਨਿਰੰਕਾਰ', 'jo tuDh bhaavai saa-ee bhalee kaar. too sadaa salaamat nirankaar.19', 'Whatever pleases You, All will be blessed, You always protect us, Formless One! 19', 20);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 131, 'ਭਰੀਐ ਹਥੁ ਪੈਰੁ ਤਨੁ ਦੇਹ ਪਾਣੀ ਧੋਤੈ ਉਤਰਸੁ ਖੇਹ', 'bharee-ai hath pair tan dayh. paanee Dhotai utras khayh.', 'Hands, feet and the body that are polluted, With water the dirt can be washed away.', 21);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 132, 'ਮੂਤ ਪਲੀਤੀ ਕਪੜੁ ਹੋਇ ਦੇ ਸਾਬੂਣੁ ਲਈਐ ਓਹੁ ਧੋਇ', 'moot paleetee kaparh ho-ay. day saaboon la-ee-ai oh Dho-ay.', 'Clothes that are soiled and stained by urine, With soap they can be washed clean.', 21);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 133, 'ਭਰੀਐ ਮਤਿ ਪਾਪਾ ਕੈ ਸੰਗਿ ਓਹੁ ਧੋਪੈ ਨਾਵੈ ਕੈ ਰੰਗਿ', 'bharee-ai mat paapaa kai sang. oh Dhopai naavai kai rang.', 'When the intellect is stained and polluted by sin, That can be cleansed by the Color of the Naam.', 21);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 134, 'ਪੁੰਨੀ ਪਾਪੀ ਆਖਣੁ ਨਾਹਿ ਕਰਿ ਕਰਿ ਕਰਣਾ ਲਿਖਿ ਲੈ ਜਾਹੁ', 'punnee paapee aakhan naahi. kar kar karnaa likh lai jaahu.', 'Virtue and vice do not come by mere words; actions repeated, again and again, are engraved on the soul.', 21);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 135, 'ਆਪੇ ਬੀਜਿ ਆਪੇ ਹੀ ਖਾਹੁ ਨਾਨਕ ਹੁਕਮੀ ਆਵਹੁ ਜਾਹੁ', 'aapay beej aapay hee khaahu. naanak hukmee aavhu jaahu. 20', 'What you plant is what you shall harvest.O Nanak, by God''s Command, we come and go. 20', 21);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 136, 'ਤੀਰਥੁ ਤਪੁ ਦਇਆ ਦਤੁ ਦਾਨੁ ਜੇ ਕੋ ਪਾਵੈ ਤਿਲ ਕਾ ਮਾਨੁ', 'tirath tap da-i-aa dat daan. jay ko paavai til kaa maan.', 'Pilgrimages, austere discipline, compassion and charity. these, by themselves, bring only an iota of merit.', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 137, 'ਸੁਣਿਆ ਮੰਨਿਆ ਮਨਿ ਕੀਤਾ ਭਾਉ ਅੰਤਰਗਤਿ ਤੀਰਥਿ ਮਲਿ ਨਾਉ', 'suni-aa mani-aa man keetaa bhaa-o. antargat tirath mal naa-o.', 'By listening and believing bring humility in your mind; Within the sacred inner shrine, cleanse your mind.', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 138, 'ਸਭਿ ਗੁਣ ਤੇਰੇ ਮੈ ਨਾਹੀ ਕੋਇ ਵਿਣੁ ਗੁਣ ਕੀਤੇ ਭਗਤਿ ਨ ਹੋਇ', 'sabh gun tayray mai naahee ko-ay. vin gun keetay bhagat na ho-ay.', 'Lord, all virtues are Yours, none are mine. Without good deeds, there is no worship.', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 139, 'ਸੁਅਸਤਿ ਆਥਿ ਬਾਣੀ ਬਰਮਾਉ ਸਤਿ ਸੁਹਾਣੁ ਸਦਾ ਮਨਿ ਚਾਉ', 'su-asat aath banee barmaa-o. sat suhaan sadaa man chaa-o.', 'Forever vibrate the Bani of Brahma. O Mind, forever seek the Companionship of Truth.', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 140, 'ਕਵਣੁ ਸੁ ਵੇਲਾ ਵਖਤੁ ਕਵਣੁ ਕਵਣ ਥਿਤਿ ਕਵਣੁ ਵਾਰੁ ਕਵਣਿ ਸਿ ਰੁਤੀ ਮਾਹੁ ਕਵਣੁ ਜਿਤੁ ਹੋਆ ਆਕਾਰੁ', 'kavan so vaylaa vakhat kavan kavan thit kavan vaar. kavan se rutee maahu kavan jit ho-aa aakaar.', 'What is that time, that moment? What is that day, that date? What is that season, that month, when the Universe was created?', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 141, 'ਵੇਲ ਨ ਪਾਈਆ ਪੰਡਤੀ ਜਿ ਹੋਵੈ ਲੇਖੁ ਪੁਰਾਣੁ ਵਖਤੁ ਨ ਪਾਇਓ ਕਾਦੀਆ ਜਿ ਲਿਖਨਿ ਲੇਖੁ ਕੁਰਾਣੁ', 'vayl na paa-ee-aa pandtee je hovai laykh puraan. vakhat na paa-i-o kaadee-aa je likhan laykh kuraan.', 'The Pandits, who have the writings of the Puraans, cannot provide this time. The time is not known to the Qazis, who study the Koran.', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 142, 'ਥਿਤਿ ਵਾਰੁ ਨਾ ਜੋਗੀ ਜਾਣੈ ਰੁਤਿ ਮਾਹੁ ਨਾ ਕੋਈ ਜਾ ਕਰਤਾ ਸਿਰਠੀ ਕਉ ਸਾਜੇ ਆਪੇ ਜਾਣੈ ਸੋਈ', 'thit vaar naa jogee jaanai rut maahu naa ko-ee. jaa kartaa sirthee ka-o saajay aapay jaanai so-ee.', 'The day and date are not known to the Yogis, nor is the month or the season. The Creator who created this creation only He Himself knows.', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 143, 'ਕਿਵ ਕਰਿ ਆਖਾ ਕਿਵ ਸਾਲਾਹੀ ਕਿਉ ਵਰਨੀ ਕਿਵ ਜਾਣਾ ਨਾਨਕ ਆਖਣਿ ਸਭੁ ਕੋ ਆਖੈ ਇਕ ਦੂ ਇਕੁ ਸਿਆਣਾ', 'kiv kar aakhaa kiv saalaahee ki-o varnee kiv jaanaa. naanak aakhan sabh ko aakhai ik doo ik si-aanaa.', 'How does one describe or give an opinion of Him or praise or get to know Him? Nanak, everyone speaks of Him, one wiser than the next!', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 144, 'ਵਡਾ ਸਾਹਿਬੁ ਵਡੀ ਨਾਈ ਕੀਤਾ ਜਾ ਕਾ ਹੋਵੈ ਨਾਨਕ ਜੇ ਕੋ ਆਪੌ ਜਾਣੈ ਅਗੈ ਗਇਆ ਨ ਸੋਹੈ. 21', 'vadaa saahib vadee naa-ee keetaa jaa kaa hovai. naanak jay ko aapou jaanai agai ga-i-aa na sohai. 21', 'The Great Master with a Great Name by His doing - everything happens. O Nanak, one who thinks he knows all, shall not move forwarded. 21', 22);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 145, 'ਪਾਤਾਲਾ ਪਾਤਾਲ ਲਖ ਆਗਾਸਾ ਆਗਾਸ', 'paataalaa paataal lakh aagaasaa aagaas.', 'Planets upon planets and hundreds of thousands of skies upon skies.', 23);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 146, 'ਓੜਕ ਓੜਕ ਭਾਲਿ ਥਕੇ ਵੇਦ ਕਹਨਿ ਇਕ ਵਾਤ', 'orhak orhak bhaal thakay vayd kahan ik vaat.', 'Forever and ever, one can search until one grows tired, the Vedas say one thing;', 23);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 147, 'ਸਹਸ ਅਠਾਰਹ ਕਹਨਿ ਕਤੇਬਾ ਅਸੁਲੂ ਇਕੁ ਧਾਤੁ', 'sahas athaarah kahan kataybaa asuloo ik Dhaat.', 'The scriptures speak of 18,000 worlds, in reality there is only One Universe.', 23);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 148, 'ਲੇਖਾ ਹੋਇ ਤ ਲਿਖੀਐ ਲੇਖੈ ਹੋਇ ਵਿਣਾਸੁ', 'laykhaa ho-ay ta likee-ai laykhai ho-ay vinaas.', 'If one had the knowledge, one could write the account, but it could not be finished.', 23);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 149, 'ਨਾਨਕ ਵਡਾ ਆਖੀਐ ਆਪੇ ਜਾਣੈ ਆਪੁ', 'naanak vadaa aakhee-ai aapay jaanai aap.22', 'Nanak, call Him Great, for He Himself knows all. 22', 23);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 150, 'ਸਾਲਾਹੀ ਸਾਲਾਹਿ ਏਤੀ ਸੁਰਤਿ ਨ ਪਾਈਆ', 'saalaahee saalaahi aytee surat na paa-ee-aa.', 'The praisers praise Him, but this is not how understanding is obtained!', 24);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 151, 'ਨਦੀਆ ਅਤੈ ਵਾਹ ਪਵਹਿ ਸਮੁੰਦਿ ਨ ਜਾਣੀਅਹਿ', 'nadee-aa atai vaah pavahi samund na jaanee-ahi.', 'The streams and rivers entering the ocean do not know its vastness.', 24);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 152, 'ਸਮੁੰਦ ਸਾਹ ਸੁਲਤਾਨ ਗਿਰਹਾ ਸੇਤੀ ਮਾਲੁ ਧਨੁ', 'samund saah sultaan girhaa saytee maal Dhan.', 'Ocean sized kingdoms owners with mountains of material wealth.', 24);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 153, 'ਕੀੜੀ ਤੁਲਿ ਨ ਹੋਵਨੀ ਜੇ ਤਿਸੁ ਮਨਹੁ ਨ ਵੀਸਰਹਿ', 'keerhee tul na hovnee jay tis manhu na veesrahi. 23', 'Even an ant outweighs them, if the Lord doesn’t dwell in their mind. 23', 24);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 154, 'ਅੰਤੁ ਨ ਸਿਫਤੀ ਕਹਣਿ ਨ ਅੰਤੁ ਅੰਤੁ ਨ ਕਰਣੈ ਦੇਣਿ ਨ ਅੰਤੁ', 'ant na siftee kahan na ant. ant na karnai dayn na ant.', 'Countless don’t Praise Him; Countless don’t speak. Countless don’t act; Countless don’t give.', 25);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 155, 'ਅੰਤੁ ਨ ਵੇਖਣਿ ਸੁਣਣਿ ਨ ਅੰਤੁ ਅੰਤੁ ਨ ਜਾਪੈ ਕਿਆ ਮਨਿ ਮੰਤੁ', 'ant na vaykhan sunan na ant. ant na jaapai ki-aa man mant.', 'Countless don’t see Him; Countless don’t listen. Countless don’t worship Him, What’s in their mind?', 25);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 156, 'ਅੰਤੁ ਨ ਜਾਪੈ ਕੀਤਾ ਆਕਾਰੁ ਅੰਤੁ ਨ ਜਾਪੈ ਪਾਰਾਵਾਰੁ', 'ant na jaapai keetaa aakaar. ant na jaapai paaraavaar.', 'Countless don’t realise the doings of the Almighty. Countless don’t know of His scope and presence.', 25);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 157, 'ਅੰਤ ਕਾਰਣਿ ਕੇਤੇ ਬਿਲਲਾਹਿ ਤਾ ਕੇ ਅੰਤ ਨ ਪਾਏ ਜਾਹਿ', 'ant kaaran kaytay billaahi. taa kay ant na paa-ay jaahi.', 'Countless yearn and struggle to know His limits, but His limits cannot be found.', 25);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 158, 'ਏਹੁ ਅੰਤੁ ਨ ਜਾਣੈ ਕੋਇ ਬਹੁਤਾ ਕਹੀਐ ਬਹੁਤਾ ਹੋਇ', 'ayhu ant na jaanai ko-ay. bahutaa kahee-ai bahutaa ho-ay.', 'No one knows these Limits. The more you describe, the more there is to be known.', 25);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 159, 'ਵਡਾ ਸਾਹਿਬੁ ਊਚਾ ਥਾਉ ਊਚੇ ਉਪਰਿ ਊਚਾ ਨਾਉ', 'vadaa saahib oochaa thaa-o. oochay upar oochaa naa-o.', 'Great is the Master high is His status. Highest of the High, and still higher is His Name.', 25);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 160, 'ਏਵਡੁ ਊਚਾ ਹੋਵੈ ਕੋਇ ਤਿਸੁ ਊਚੇ ਕਉ ਜਾਣੈ ਸੋਇ', 'ayvad oochaa hovai ko-ay. tis oochay ka-o jaanai so-ay.', 'Only a few can get this high, His Lofty and Exalted state is only known by them.', 25);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 161, 'ਜੇਵਡੁ ਆਪਿ ਜਾਣੈ ਆਪਿ ਆਪਿ ਨਾਨਕ ਨਦਰੀ ਕਰਮੀ ਦਾਤਿ', 'jayvad aap jaanai aap aap. naanak nadree karmee daat. 24', 'How Great He is? Only He knows Himself. Nanak, only by His Grace and Doing do we receive His Gifts. 24', 25);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 162, 'ਬਹੁਤਾ ਕਰਮੁ ਲਿਖਿਆ ਨਾ ਜਾਇ ਵਡਾ ਦਾਤਾ ਤਿਲੁ ਨ ਤਮਾਇ', 'bahutaa karam likhi-aa naa jaa-ay. vadaa daataa til na tamaa-ay.', 'His doings are beyond our written record. The Great Giver does not hold anything back.', 26);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 163, 'ਕੇਤੇ ਮੰਗਹਿ ਜੋਧ ਅਪਾਰ ਕੇਤਿਆ ਗਣਤ ਨਹੀ ਵੀਚਾਰੁ', 'kaytay mangahi joDh apaar.kayti-aa ganat nahee veechaar.', 'Where will the great warriors beg? So many do not contemplate or dwell upon Him.', 26);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 164, 'ਕੇਤੇ ਖਪਿ ਤੁਟਹਿ ਵੇਕਾਰ ਕੇਤੇ ਲੈ ਲੈ ਮੁਕਰੁ ਪਾਹਿ', 'kaytay khap tutahi vaykaar. kaytay lai lai mukar paahi.', 'How many in pain break and waste away? How many keep taking over and over again, and then deny it?', 26);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 165, 'ਕੇਤੇ ਮੂਰਖ ਖਾਹੀ ਖਾਹਿ ਕੇਤਿਆ ਦੂਖ ਭੂਖ ਸਦ ਮਾਰ', 'kaytay moorakh khaahee khaahi. kayti-aa dookh bhookh sad maar.', 'How many fools keep continuously consuming? So many endure pain, deprivation and constant abuse.', 26);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 166, 'ਏਹਿ ਭਿ ਦਾਤਿ ਤੇਰੀ ਦਾਤਾਰ ਬੰਦਿ ਖਲਾਸੀ ਭਾਣੈ ਹੋਇ', 'ayhi bhe daat tayree daataar. band khalaasee bhaanai ho-ay.', 'Even these are Your Gifts, Great Giver! Liberation from bondage comes only by Your Will.', 26);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 167, 'ਹੋਰੁ ਆਖਿ ਨ ਸਕੈ ਕੋਇ ਜੇ ਕੋ ਖਾਇਕੁ ਆਖਣਿ ਪਾਇ ਓਹੁ ਜਾਣੈ ਜੇਤੀਆ ਮੁਹਿ ਖਾਇ', 'hor aakh na sakai ko-ay. jay ko khaa-ik aakhan paa-ay. oh jaanai jaytee-aa muhi khaa-ay.', 'No one else has any say in this. If some fool should say otherwise, he certainly will have to eat his words.', 26);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 168, 'ਆਪੇ ਜਾਣੈ ਆਪੇ ਦੇਇ ਆਖਹਿ ਸਿ ਭਿ ਕੇਈ ਕੇਇ', 'aapay jaanai aapay day-ay. aakhahi se bhe kay-ee kay-ay.', 'He Himself knows, He Himself gives. Few, very few acknowledge this.', 26);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 169, 'ਜਿਸ ਨੋ ਬਖਸੇ ਸਿਫਤਿ ਸਾਲਾਹ ਨਾਨਕ ਪਾਤਿਸਾਹੀ ਪਾਤਿਸਾਹੁ', 'jis no bakhsay sifat saalaah. naanak paatisaahee paatisaahu. 25', 'One, who is blessed to sing His Praises Nanak, is the king of kings. 25', 26);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 170, 'ਅਮੁਲ ਗੁਣ ਅਮੁਲ ਵਾਪਾਰ ਅਮੁਲ ਵਾਪਾਰੀਏ ਅਮੁਲ ਭੰਡਾਰ', 'amul gun amul vaapaar. amul vaapaaree-ay amul bhandaar.', 'Priceless are Virtues, Priceless are Dealings. Priceless are Dealers, Priceless are Treasures.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 171, 'ਅਮੁਲ ਆਵਹਿ ਅਮੁਲ ਲੈ ਜਾਹਿ ਅਮੁਲ ਭਾਇ ਅਮੁਲਾ ਸਮਾਹਿ', 'amul aavahi amul lai jaahi. amul bhaa-ay amulaa samaahi.', 'Priceless are the comings; Priceless are taken away. Priceless is Love for Him, Priceless are absorbed.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 172, 'ਅਮੁਲੁ ਧਰਮੁ ਅਮੁਲੁ ਦੀਬਾਣੁ ਅਮੁਲੁ ਤੁਲੁ ਅਮੁਲੁ ਪਰਵਾਣੁ', 'amul Dharam amul deebaan. amul tul amul parvaan.', 'Priceless is Dharma, Priceless is His Court. Priceless are the scales priceless are the weights.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 173, 'ਅਮੁਲੁ ਬਖਸੀਸ ਅਮੁਲੁ ਨੀਸਾਣੁ ਅਮੁਲੁ ਕਰਮੁ ਅਮੁਲੁ ਫੁਰਮਾਣੁ', 'amul bakhsees amul neesaan. amul karam amul furmaan.', 'Priceless are His Blessings, Priceless is the Banner and Insignia. Priceless is His Mercy, Priceless is His Royal Command.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 174, 'ਅਮੁਲੋ ਅਮੁਲੁ ਆਖਿਆ ਨ ਜਾਇ ਆਖਿ ਆਖਿ ਰਹੇ ਲਿਵ ਲਾਇ', 'amulo amul aakhi-aa na jaa-ay. aakh aakh rahay liv laa-ay.', 'Priceless, O Priceless beyond expression! By repeating continually remain absorbed in His Love.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 175, 'ਆਖਹਿ ਵੇਦ ਪਾਠ ਪੁਰਾਣ ਆਖਹਿ ਪੜੇ ਕਰਹਿ ਵਖਿਆਣ ਆਖਹਿ ਬਰਮੇ ਆਖਹਿ ਇੰਦ', 'aakhahi vayd paath puraan. aakhahi parhay karahi vakhi-aan. aakhahi barmay aakhahi ind.', 'The Vedas and Puraanas speak. The scholars speak and lecture. Brahma speaks Indra speaks.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 176, 'ਆਖਹਿ ਗੋਪੀ ਤੈ ਗੋਵਿੰਦ ਆਖਹਿ ਈਸਰ ਆਖਹਿ ਸਿਧ ਆਖਹਿ ਕੇਤੇ ਕੀਤੇ ਬੁਧ', 'aakhahi gopee tai govind. aakhahi eesar aakhahi siDh. aakhahi kaytay keetay buDh.', 'The Gopis and Krishna speak. Shiva speaks the Siddhas speak. The many created Buddhas speak.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 177, 'ਆਖਹਿ ਦਾਨਵ ਆਖਹਿ ਦੇਵ ਆਖਹਿ ਸੁਰਿ ਨਰ ਮੁਨਿ ਜਨ ਸੇਵ', 'aakhahi daanav aakhahi dayv. aakhahi sur nar mun jan sayv.', 'The demons speak the demi-gods speak. The warriors, the heavenly beings, the silent sages, the humble Sewadars speak.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 178, 'ਕੇਤੇ ਆਖਹਿ ਆਖਣਿ ਪਾਹਿ ਕੇਤੇ ਕਹਿ ਕਹਿ ਉਠਿ ਉਠਿ ਜਾਹਿ', 'kaytay aakhahi aakhan paahi. kaytay kahi kahi uth uth jaahi.', 'Many say He is obtained by speaking. Many having spoken over and over have arisen and departed.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 179, 'ਏਤੇ ਕੀਤੇ ਹੋਰਿ ਕਰੇਹਿ ਤਾ ਆਖਿ ਨ ਸਕਹਿ ਕੇਈ ਕੇਇ', 'aytay keetay hor karayhi. taa aakh na sakahi kay-ee kay-ay.', 'Having done all this, have it done again; even then, they could not describe Him.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 180, 'ਜੇਵਡੁ ਭਾਵੈ ਤੇਵਡੁ ਹੋਇ ਨਾਨਕ ਜਾਣੈ ਸਾਚਾ ਸੋਇ', 'jayvad bhaavai tayvad ho-ay. naanak jaanai saachaa so-ay.', 'Whatever the Great one desires so shall transpire. O Nanak, Only the True Lord knows.', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 181, 'ਜੇ ਕੋ ਆਖੈ ਬੋਲੁਵਿਗਾੜੁ ਤਾ ਲਿਖੀਐ ਸਿਰਿ ਗਾਵਾਰਾ ਗਾਵਾਰੁ', 'jay ko aakhai boluvigaarh. taa likee-ai sir gaavaaraa gaavaar. 26', 'If anyone presumes to describe God, he shall be known as the greatest fool of fools! 26', 27);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 182, 'ਸੋ ਦਰੁ ਕੇਹਾ ਸੋ ਘਰੁ ਕੇਹਾ ਜਿਤੁ ਬਹਿ ਸਰਬ ਸਮਾਲੇ', 'so dar kayhaa so ghar kayhaa jit bahi sarab samaalay.', 'What is that Place like? What is the House like? Where that Lord resides?', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 183, 'ਵਾਜੇ ਨਾਦ ਅਨੇਕ ਅਸੰਖਾ ਕੇਤੇ ਵਾਵਣਹਾਰੇ ਕੇਤੇ ਰਾਗ ਪਰੀ ਸਿਉ ਕਹੀਅਨਿ ਕੇਤੇ ਗਾਵਣਹਾਰੇ', 'vaajay naad anayk asankhaa kaytay vaavanhaaray. kaytay raag paree si-o kahee-an kaytay gaavanhaaray.', 'Numerous Sound-currents vibrate simultaneously - Where are the musicians? So many melodious Ragas are sung – Where are the singers?', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 184, 'ਗਾਵਹਿ ਤੁਹਨੋ ਪਉਣੁ ਪਾਣੀ ਬੈਸੰਤਰੁ ਗਾਵੈ ਰਾਜਾ ਧਰਮੁ ਦੁਆਰੇ', 'gaavahi tuhno pa-un paanee baisantar gaavai raajaa Dharam du-aaray.','Singing for You are the wind, water and fire; the King Dharma sings at Your Door.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 185, 'ਗਾਵਹਿ ਚਿਤੁ ਗੁਪਤੁ ਲਿਖਿ ਜਾਣਹਿ ਲਿਖਿ ਲਿਖਿ ਧਰਮੁ ਵੀਚਾਰੇ', 'gaavahi chit gupat likh jaaneh likh likh Dharam veechaaray.', 'Chitr and Gupt, the angels of the conscious and subconscious who record actions sing; Dharma keeps writing, then thinks.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 186, 'ਗਾਵਹਿ ਈਸਰੁ ਬਰਮਾ ਦੇਵੀ ਸੋਹਨਿ ਸਦਾ ਸਵਾਰੇ ਗਾਵਹਿ ਇੰਦ ਇਦਾਸਣਿ ਬੈਠੇ ਦੇਵਤਿਆ ਦਰਿ ਨਾਲੇ', 'gaavahi eesar barmaa dayvee sohan sadaa savaaray. gaavahi ind idaasan baithay dayviti-aa dar naalay.', 'Sing, Shiva, Brahma and Devi, beautifully decorated. Sing, Indra, seated upon His Throne, with the deities at Your Door.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 187, 'ਗਾਵਹਿ ਸਿਧ ਸਮਾਧੀ ਅੰਦਰਿ ਗਾਵਨਿ ਸਾਧ ਵਿਚਾਰੇ', 'gaavahi siDh samaaDhee andar gaavan saaDh vichaaray.', 'Sing, Yogis in their posture/trance; the holy priests in contemplation.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 188, 'ਗਾਵਨਿ ਜਤੀ ਸਤੀ ਸੰਤੋਖੀ ਗਾਵਹਿ ਵੀਰ ਕਰਾਰੇ ਗਾਵਨਿ ਪੰਡਿਤ ਪੜਨਿ ਰਖੀਸਰ ਜੁਗੁ ਜੁਗੁ ਵੇਦਾ ਨਾਲੇ', 'gaavan jatee satee santokhee gaavahi veer karaaray.gaavan pandit parhan rakheesar jug jug vaydaa naalay.', 'Sing, celibates, fanatics and the peaceful; Sing, the fearless warriors. Sing, Pandits, the religious scholars who have studied the Vedas for many ages.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 189, 'ਗਾਵਹਿ ਮੋਹਣੀਆ ਮਨੁ ਮੋਹਨਿ ਸੁਰਗਾ ਮਛ ਪਇਆਲੇ', 'gaavahi mohnee-aa man mohan surgaa machh pa-i-aalay.', 'Sing, Mohinis, the enticer of hearts in this world, in paradise, and in the underworld of the subconscious.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 190, 'ਗਾਵਨਿ ਰਤਨ ਉਪਾਏ ਤੇਰੇ ਅਠਸਠਿ ਤੀਰਥ ਨਾਲੇ ਗਾਵਹਿ ਜੋਧ ਮਹਾਬਲ ਸੂਰਾ ਗਾਵਹਿ ਖਾਣੀ ਚਾਰੇ', 'gaavan ratan upaa-ay tayray athsath tirath naalay. gaavahi joDh mahaabal sooraa gaavahi khaanee chaaray.', 'Sing, celestial jewels created by You, with the 68 holy places of pilgrimage. Sing, brave mighty warriors, Sing, the four sources of creation.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 191, 'ਗਾਵਹਿ ਖੰਡ ਮੰਡਲ ਵਰਭੰਡਾ ਕਰਿ ਕਰਿ ਰਖੇ ਧਾਰੇ', 'gaavahi khand mandal varbhandaa kar kar rakhay Dhaaray.', 'Sing, the planets, solar systems and galaxies, created and arranged by Your Hand.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 192, 'ਸੇਈ ਤੁਧੁਨੋ ਗਾਵਹਿ ਜੋ ਤੁਧੁ ਭਾਵਨਿ ਰਤੇ ਤੇਰੇ ਭਗਤ ਰਸਾਲੇ', 'say-ee tuDhuno gaavahi jo tuDh bhaavan ratay tayray bhagat rasaalay.', 'Only those alone sing, who yearn for You, Your immersed and purified Bhagats.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 193, 'ਹੋਰਿ ਕੇਤੇ ਗਾਵਨਿ ਸੇ ਮੈ ਚਿਤਿ ਨ ਆਵਨਿ ਨਾਨਕੁ ਕਿਆ ਵੀਚਾਰੇ', 'hor kaytay gaavan say mai chit na aavan naanak ki-aa veechaaray.', 'Who else sings? They do not come to mind. O Nanak, What can be considered?', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 194, 'ਸੋਈ ਸੋਈ ਸਦਾ ਸਚੁ ਸਾਹਿਬੁ ਸਾਚਾ ਸਾਚੀ ਨਾਈ ਹੈ ਭੀ ਹੋਸੀ ਜਾਇ ਨ ਜਾਸੀ ਰਚਨਾ ਜਿਨਿ ਰਚਾਈ', 'so-ee so-ee sadaa sach saahib saachaa saachee naa-ee. hai bhee hosee jaa-ay na jaasee rachnaa jin rachaa-ee.', 'He and only He, the True Lord is always True and True is His Name. He is, and shall always be. He shall never depart - the Creator of the Universe.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 195, 'ਰੰਗੀ ਰੰਗੀ ਭਾਤੀ ਕਰਿ ਕਰਿ ਜਿਨਸੀ ਮਾਇਆ ਜਿਨਿ ਉਪਾਈ', 'rangee rangee bhaatee kar kar jinsee maa-i-aa jin upaa-ee.', 'He created the world, with its various colors, species of beings, and the variety of Maya.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 196, 'ਕਰਿ ਕਰਿ ਵੇਖੈ ਕੀਤਾ ਆਪਣਾ ਜਿਵ ਤਿਸ ਦੀ ਵਡਿਆਈ ਜੋ ਤਿਸੁ ਭਾਵੈ ਸੋਈ ਕਰਸੀ ਹੁਕਮੁ ਨ ਕਰਣਾ ਜਾਈ', 'kar kar vaykhai keetaa aapnaa jiv tis dee vadi-aa-ee. jo tis bhaavai so-ee karsee hukam na karnaa jaa-ee.', 'Having created the creation, He watches over it Himself, by His Greatness. He does whatever He pleases. No order can be issued to Him.', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 197, 'ਸੋ ਪਾਤਿਸਾਹੁ ਸਾਹਾ ਪਾਤਿਸਾਹਿਬੁ ਨਾਨਕ ਰਹਣੁ ਰਜਾਈ', 'so paatisaahu saahaa paatisaahib naanak rahan rajaa-ee. 27', 'He is the King - the King of kings. Nanak remains submissive to His Will. 27', 28);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 198, 'ਮੁੰਦਾ ਸੰਤੋਖੁ ਸਰਮੁ ਪਤੁ ਝੋਲੀ ਧਿਆਨ ਕੀ ਕਰਹਿ ਬਿਭੂਤਿ', 'munda santokh saram pat jholee Dhi-aan kee karahi bibhoot.', 'Make contentment your ear-rings, humility your begging bowl and meditation the body ash.', 29);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 199, 'ਖਿੰਥਾ ਕਾਲੁ ਕੁਆਰੀ ਕਾਇਆ ਜੁਗਤਿ ਡੰਡਾ ਪਰਤੀਤਿ', 'khinthaa kaal ku-aaree kaa-i-aa jugat dandaa parteet.', 'The thought of death; aspiration of the bride to be; and faith in the Lord be your walking stick.', 29);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 200, 'ਆਈ ਪੰਥੀ ਸਗਲ ਜਮਾਤੀ ਮਨਿ ਜੀਤੈ ਜਗੁ ਜੀਤੁ', 'aa-ee panthee sagal jamaatee man jeetai jag jeet.', 'Come as a traveller; All are fellow students; conquer your mind, and conquer the world.', 29);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 201, 'ਆਦੇਸੁ ਤਿਸੈ ਆਦੇਸੁ', 'aadays tisai aadays.', 'Salutation! My humbly salutation to You.', 29);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 202, 'ਆਦਿ ਅਨੀਲੁ ਅਨਾਦਿ ਅਨਾਹਤਿ ਜੁਗੁ ਜੁਗੁ ਏਕੋ ਵੇਸੁ', 'aad aneel anaad anaahat jug jug ayko vays. 28', 'The Primal One, the Pure Light, without beginning, without end. Throughout time you remain same. 28', 29);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 203, 'ਭੁਗਤਿ ਗਿਆਨੁ ਦਇਆ ਭੰਡਾਰਣਿ ਘਟਿ ਘਟਿ ਵਾਜਹਿ ਨਾਦ', 'bhugat gi-aan da-i-aa bhandaaran ghat ghat vaajeh naad.', 'Make wisdom your food and compassion your attendant; The Sound-current vibrates in each and every heart.', 30);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 204, 'ਆਪਿ ਨਾਥੁ ਨਾਥੀ ਸਭ ਜਾ ਕੀ ਰਿਧਿ ਸਿਧਿ ਅਵਰਾ ਸਾਦ', 'aap naath naathee sabh jaa kee riDh siDh avraa saad.', 'You are Supreme Lord of all; wealth and miraculous spiritual powers are the tastes of others.', 30);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 205, 'ਸੰਜੋਗੁ ਵਿਜੋਗੁ ਦੁਇ ਕਾਰ ਚਲਾਵਹਿ ਲੇਖੇ ਆਵਹਿ ਭਾਗ', 'sanjog vijog du-ay kaar chalaaveh laykhay aavahi bhaag.', 'Union and separation both drive the world; only our destiny matters.', 30);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 206, 'ਆਦੇਸੁ ਤਿਸੈ ਆਦੇਸੁ', 'aadays tisai aadays.', 'Salutation! My humbly salutation to You.', 30);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 207, 'ਆਦਿ ਅਨੀਲੁ ਅਨਾਦਿ ਅਨਾਹਤਿ ਜੁਗੁ ਜੁਗੁ ਏਕੋ ਵੇਸੁ', 'aad aneel anaad anaahat jug jug ayko vays. 29', 'The Primal One, the Pure Light, without beginning, without end. Throughout time you remain same. 29', 30);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 208, 'ਏਕਾ ਮਾਈ ਜੁਗਤਿ ਵਿਆਈ ਤਿਨਿ ਚੇਲੇ ਪਰਵਾਣੁ', 'aykaa maa-ee jugat vi-aa-ee tin chaylay parvaan.', 'The One Divine Mother conceived and gave birth to the three deities.', 31);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 209, 'ਇਕੁ ਸੰਸਾਰੀ ਇਕੁ ਭੰਡਾਰੀ ਇਕੁ ਲਾਏ ਦੀਬਾਣੁ', 'ik sansaaree ik bhandaaree ik laa-ay deebaan.', 'One the Creator of the World; One the Sustainer; and One the Destroyer.', 31);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 210, 'ਜਿਵ ਤਿਸੁ ਭਾਵੈ ਤਿਵੈ ਚਲਾਵੈ ਜਿਵ ਹੋਵੈ ਫੁਰਮਾਣੁ', 'jiv tis bhaavai tivai chalaavai jiv hovai furmaan.', 'Whatever He wishes, He makes to happen according to His Command.', 31);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 211, 'ਓਹੁ ਵੇਖੈ ਓਨਾ ਨਦਰਿ ਨ ਆਵੈ ਬਹੁਤਾ ਏਹੁ ਵਿਡਾਣੁ', 'oh vaykhai onaa nadar na aavai bahutaa ayhu vidaan.', 'He watches all, but none see Him! How mysterious is this?', 31);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 212, 'ਆਦੇਸੁ ਤਿਸੈ ਆਦੇਸੁ', 'aadays tisai aadays.', 'Salutation! My humbly salutation to You.', 31);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 213, 'ਆਦਿ ਅਨੀਲੁ ਅਨਾਦਿ ਅਨਾਹਤਿ ਜੁਗੁ ਜੁਗੁ ਏਕੋ ਵੇਸੁ', 'aad aneel anaad anaahat jug jug ayko vays. 30', 'The Primal One, the Pure Light, without beginning, without end. Throughout time you remain same. 30', 31);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 214, 'ਆਸਣੁ ਲੋਇ ਲੋਇ ਭੰਡਾਰ', 'aasan lo-ay lo-ay bhandaar.', 'World upon world are His Seats of Authority and His Stores.', 32);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 215, 'ਜੋ ਕਿਛੁ ਪਾਇਆ ਸੁ ਏਕਾ ਵਾਰ', 'jo kichh paa-i-aa so aykaa vaar.', 'Whatever was put into them, was put there once and for all.', 32);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 216, 'ਕਰਿ ਕਰਿ ਵੇਖੈ ਸਿਰਜਣਹਾਰੁ', 'kar kar vaykhai sirjanhaar.', 'Having created the Universe, He watches over it.', 32);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 217, 'ਨਾਨਕ ਸਚੇ ਕੀ ਸਾਚੀ ਕਾਰ', 'naanak sachay kee saachee kaar.', 'Nanak, This is the True Creator’s True Creation.', 32);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 218, 'ਆਦੇਸੁ ਤਿਸੈ ਆਦੇਸੁ', 'aadays tisai aadays.', 'Salutation! My humbly salutation to You.', 32);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 219, 'ਆਦਿ ਅਨੀਲੁ ਅਨਾਦਿ ਅਨਾਹਤਿ ਜੁਗੁ ਜੁਗੁ ਏਕੋ ਵੇਸੁ', 'aad aneel anaad anaahat jug jug ayko vays. 31', 'The Primal One, the Pure Light, without beginning, without end. Throughout time you remain same. 31', 32);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 220, 'ਇਕ ਦੂ ਜੀਭੌ ਲਖ ਹੋਹਿ ਲਖ ਹੋਵਹਿ ਲਖ ਵੀਸ', 'ik doo jeebhou lakh hohi lakh hoveh lakh vees.', 'From the one tongue, if I had 100,000 tongues, and these were then multiplied by twenty more.', 33);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 221, 'ਲਖੁ ਲਖੁ ਗੇੜਾ ਆਖੀਅਹਿ ਏਕੁ ਨਾਮੁ ਜਗਦੀਸ', 'lakh lakh gayrhaa aakhee-ahi ayk naam jagdees.', 'I would repeat, hundreds of thousands of times, the One Name of the Creator.', 33);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 222, 'ਏਤੁ ਰਾਹਿ ਪਤਿ ਪਵੜੀਆ ਚੜੀਐ ਹੋਇ ਇਕੀਸ', 'ayt raahi pat pavrhee-aa charhee-ai ho-ay ikees.', 'Tread this path to our Husband Lord, climbing these steps one merges with Him.', 33);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 223, 'ਸੁਣਿ ਗਲਾ ਆਕਾਸ ਕੀ ਕੀਟਾ ਆਈ ਰੀਸ', 'sun galaa aakaas kee keetaa aa-ee rees.', 'Hearing of talks of the cosmos, even worms want to compete.', 33);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 224, 'ਨਾਨਕ ਨਦਰੀ ਪਾਈਐ ਕੂੜੀ ਕੂੜੈ ਠੀਸ', 'naanak nadree paa-ee-ai koorhee koorhai thees. 32', 'Nanak, Only by His Grace is He obtained. The boastings of the false are a sham. 32', 33);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 225, 'ਆਖਣਿ ਜੋਰੁ ਚੁਪੈ ਨਹ ਜੋਰੁ', 'aakhan jor chupai nah jor.', 'Uttering brings power; silence brings no power.', 34);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 226, 'ਜੋਰੁ ਨ ਮੰਗਣਿ ਦੇਣਿ ਨ ਜੋਰੁ', 'jor na mangan dayn na jor.', 'Power is not gained by begging; giving doesn’t bring power.', 34);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 227, 'ਜੋਰੁ ਨ ਜੀਵਣਿ ਮਰਣਿ ਨਹ ਜੋਰੁ', 'jor na jeevan maran nah jor.', 'Power is not gained by living; death doesn’t bring power.', 34);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 228, 'ਜੋਰੁ ਨ ਰਾਜਿ ਮਾਲਿ ਮਨਿ ਸੋਰੁ', 'jor na raaj maal man sor.', 'Power is not gained by ruling with wealth and occult mental powers.', 34);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 229, 'ਜੋਰੁ ਨ ਸੁਰਤੀ ਗਿਆਨਿ ਵੀਚਾਰਿ', 'jor na surtee gi-aan veechaar.', 'Power is not gained by understanding, spiritual wisdom and meditation.', 34);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 230, 'ਜੋਰੁ ਨ ਜੁਗਤੀ ਛੁਟੈ ਸੰਸਾਰੁ', 'jor na jugtee chhutai sansaar.', 'Power is not gained by finding a way to escape from the world.', 34);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 231, 'ਜਿਸੁ ਹਥਿ ਜੋਰੁ ਕਰਿ ਵੇਖੈ ਸੋਇ', 'jis hath jor kar vaykhai so-ay.', 'In whose Hand the Power is, He watches over all.', 34);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 232, 'ਨਾਨਕ ਉਤਮੁ ਨੀਚੁ ਨ ਕੋਇ', 'naanak utam neech na ko-ay. 33', 'O Nanak, no one is high or low. 33', 34);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 233, 'ਰਾਤੀ ਰੁਤੀ ਥਿਤੀ ਵਾਰ ਪਵਣ ਪਾਣੀ ਅਗਨੀ ਪਾਤਾਲ', 'raatee rutee thitee vaar. pavan paanee agnee paataal.', 'Nights, days, weeks and seasons; wind, water, fire and the nether regions', 35);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 234, 'ਤਿਸੁ ਵਿਚਿ ਧਰਤੀ ਥਾਪਿ ਰਖੀ ਧਰਮ ਸਾਲ ਤਿਸੁ ਵਿਚਿ ਜੀਅ ਜੁਗਤਿ ਕੇ ਰੰਗ', 'tis vich Dhartee thaap rakhee Dharam saal. tis vich jee-a jugat kay rang.', 'in the midst of these, He established the earth as a home for Dharma.Upon it, He placed creatures of various colors.', 35);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 235, 'ਤਿਨ ਕੇ ਨਾਮ ਅਨੇਕ ਅਨੰਤ ਕਰਮੀ ਕਰਮੀ ਹੋਇ ਵੀਚਾਰੁ', 'tin kay naam anayk anant. karmee karmee ho-ay veechaar.', 'Their names are uncountable and endless. By their karams and deeds will they be judged.', 35);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 236, 'ਸਚਾ ਆਪਿ ਸਚਾ ਦਰਬਾਰੁ ਤਿਥੈ ਸੋਹਨਿ ਪੰਚ ਪਰਵਾਣੁ', 'sachaa aap sachaa darbaar. tithai sohan panch parvaan.', 'God Himself is True, and True is His Court. There, in perfect grace sit the elect Controllers.', 35);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 237, 'ਨਦਰੀ ਕਰਮਿ ਪਵੈ ਨੀਸਾਣੁ ਕਚ ਪਕਾਈ ਓਥੈ ਪਾਇ.', 'nadree karam pavai neesaan. kach pakaa-ee othai paa-ay.', 'Through blessed Karmas, they receive their Mark. The ripe and the unripe shall be judged there.', 35);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 238, 'ਨਾਨਕ ਗਇਆ ਜਾਪੈ ਜਾਇ', 'naanak ga-i-aa jaapai jaa-ay. 34', 'O Nanak, those go there who have worshipped. 34', 35);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 239, 'ਧਰਮ ਖੰਡ ਕਾ ਏਹੋ ਧਰਮੁ ਗਿਆਨ ਖੰਡ ਕਾ ਆਖਹੁ ਕਰਮੁ', 'Dharam khand kaa ayho Dharam. gi-aan khand kaa aakhhu karam.', 'The Kingdom of Dharma has set rules. Speak of the qualities of the realm of knowledge.', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 240, 'ਕੇਤੇ ਪਵਣ ਪਾਣੀ ਵੈਸੰਤਰ ਕੇਤੇ ਕਾਨ ਮਹੇਸ', 'kaytay pavan paanee vaisantar kaytay kaan mahays.', 'So many winds, waters and fires; so many Krishnas and Shivas', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 241, 'ਕੇਤੇ ਬਰਮੇ ਘਾੜਤਿ ਘੜੀਅਹਿ ਰੂਪ ਰੰਗ ਕੇ ਵੇਸ', 'kaytay barmay ghaarhat gharhee-ahi roop rang kay vays.', 'So many Brahmas fashioning forms of great beauty, adorned and dressed in many colors.', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 242, 'ਕੇਤੀਆ ਕਰਮ ਭੂਮੀ ਮੇਰ ਕੇਤੇ ਕੇਤੇ ਧੂ ਉਪਦੇਸ', 'kaytee-aa karam bhoomee mayr kaytay kaytay Dhoo updays.', 'So many worlds and lands for working out karma. So very many lessons to be learned!', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 243, 'ਕੇਤੇ ਇੰਦ ਚੰਦ ਸੂਰ ਕੇਤੇ ਕੇਤੇ ਮੰਡਲ ਦੇਸ', 'kaytay ind chand soor kaytay kaytay mandal days.', 'So many Indras, so many moons and suns, so many worlds and lands.', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 244, 'ਕੇਤੇ ਸਿਧ ਬੁਧ ਨਾਥ ਕੇਤੇ ਕੇਤੇ ਦੇਵੀ ਵੇਸ', 'kaytay siDh buDh naath kaytay kaytay dayvee vays.', 'So many Siddhas and Buddhas, so many Yogic masters. So many goddesses of various kinds.', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 245, 'ਕੇਤੇ ਦੇਵ ਦਾਨਵ ਮੁਨਿ ਕੇਤੇ ਕੇਤੇ ਰਤਨ ਸਮੁੰਦ', 'kaytay dayv daanav mun kaytay kaytay ratan samund.', 'So many demi-gods and demons, so many silent sages. So many oceans of jewels.', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 246, 'ਕੇਤੀਆ ਖਾਣੀ ਕੇਤੀਆ ਬਾਣੀ ਕੇਤੇ ਪਾਤ ਨਰਿੰਦ', 'kaytee-aa khaanee kaytee-aa banee kaytay paat narind.', 'So many ways of life, so many languages. So many dynasties of rulers.', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 247, 'ਕੇਤੀਆ ਸੁਰਤੀ ਸੇਵਕ ਕੇਤੇ ਨਾਨਕ ਅੰਤੁ ਨ ਅੰਤੁ', 'kaytee-aa surtee sayvak kaytay naanak ant na ant. 35', 'So many intuitive people, so many selfless servants. O Nanak, Limitless is His limit! 35', 36);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 248, 'ਗਿਆਨ ਖੰਡ ਮਹਿ ਗਿਆਨੁ ਪਰਚੰਡੁ', 'gi-aan khand meh gi-aan parchand.', 'In the realm of wisdom, spiritual wisdom reigns supreme.', 37);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 249, 'ਤਿਥੈ ਨਾਦ ਬਿਨੋਦ ਕੋਡ ਅਨੰਦੁ', 'tithai naad binod kod anand.', 'The Sound current vibrates amidst the sounds and the sights of bliss.', 37);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 250, 'ਸਰਮ ਖੰਡ ਕੀ ਬਾਣੀ ਰੂਪੁ', 'saram khand kee banee roop.', 'In the realm of humility the beautiful Bani reigns.', 37);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 251, 'ਤਿਥੈ ਘਾੜਤਿ ਘੜੀਐ ਬਹੁਤੁ ਅਨੂਪੁ', 'tithai ghaarhat gharhee-ai bahut anoop.', 'Forms of incomparable beauty are created and fashioned there.', 37);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 252, 'ਤਾ ਕੀਆ ਗਲਾ ਕਥੀਆ ਨਾ ਜਾਹਿ', 'taa kee-aa galaa kathee-aa naa jaahi.', 'These things cannot be described.', 37);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 253, 'ਜੇ ਕੋ ਕਹੈ ਪਿਛੈ ਪਛੁਤਾਇ', 'jay ko kahai pichhai pachhutaa-ay.', 'One who tries to speak of these shall regret the attempt.', 37);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 254, 'ਤਿਥੈ ਘੜੀਐ ਸੁਰਤਿ ਮਤਿ ਮਨਿ ਬੁਧਿ', 'tithai gharhee-ai surat mat man buDh.', 'The intuitive consciousness, intellect and understanding of the mind are shaped there.', 37);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 255, 'ਤਿਥੈ ਘੜੀਐ ਸੁਰਾ ਸਿਧਾ ਕੀ ਸੁਧਿ', 'tithai gharhee-ai suraa siDhaa kee suDh. 36', 'The consciousness of the spiritual warriors and the beings of spiritual perfection, are shaped there. 36', 37);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 256, 'ਕਰਮ ਖੰਡ ਕੀ ਬਾਣੀ ਜੋਰੁ ਤਿਥੈ ਹੋਰੁ ਨ ਕੋਈ ਹੋਰੁ', 'karam khand kee banee jor. tithai hor na ko-ee hor.', 'In the realm of karma, the Bani is Power. Nothing else can prevail here.', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 257, 'ਤਿਥੈ ਜੋਧ ਮਹਾਬਲ ਸੂਰ ਤਿਨ ਮਹਿ ਰਾਮੁ ਰਹਿਆ ਭਰਪੂਰ', 'tithai joDh mahaabal soor. tin meh raam rahi-aa bharpoor.', 'The warriors of great power and immense courage. In whom Ram dwells and is infused completely.', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 258, 'ਤਿਥੈ ਸੀਤੋ ਸੀਤਾ ਮਹਿਮਾ ਮਾਹਿ ਤਾ ਕੇ ਰੂਪ ਨ ਕਥਨੇ ਜਾਹਿ', 'tithai seeto seetaa mahimaa maahi. taa kay roop na kathnay jaahi.', 'Myriads of Sitas are there, cool and calm in their majestic glory, Their physical features are beyond description.', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 259, 'ਨਾ ਓਹਿ ਮਰਹਿ ਨ ਠਾਗੇ ਜਾਹਿ ਜਿਨ ਕੈ ਰਾਮੁ ਵਸੈ ਮਨ ਮਾਹਿ', 'naa ohi mareh na thaagay jaahi. jin kai raam vasai man maahi.', 'Neither do they die nor can they be robbed, within whose minds the Lord Ram resides.', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 260, 'ਤਿਥੈ ਭਗਤ ਵਸਹਿ ਕੇ ਲੋਅ ਕਰਹਿ ਅਨੰਦੁ ਸਚਾ ਮਨਿ ਸੋਇ', 'tithai bhagat vaseh kay lo-a. karahi anand sachaa man so-ay.', 'The devotees of many worlds dwell there. They are in bliss; the True Lord dwells in their minds.', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 261, 'ਸਚ ਖੰਡਿ ਵਸੈ ਨਿਰੰਕਾਰੁ ਕਰਿ ਕਰਿ ਵੇਖੈ ਨਦਰਿ ਨਿਹਾਲ', 'sach khand vasai nirankaar. kar kar vaykhai nadar nihaal.', 'In the realm of Truth, the Formless Lord abides. Having created, He watches over the Creation bestowing Grace and happiness.', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 262, 'ਤਿਥੈ ਖੰਡ ਮੰਡਲ ਵਰਭੰਡ ਜੇ ਕੋ ਕਥੈ ਤ ਅੰਤ ਨ ਅੰਤ', 'tithai khand mandal varbhand. jay ko kathai ta ant na ant.', 'There are planets, solar systems and galaxies. If one describes them, there is no limit, no end.', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 263, 'ਤਿਥੈ ਲੋਅ ਲੋਅ ਆਕਾਰ ਜਿਵ ਜਿਵ ਹੁਕਮੁ ਤਿਵੈ ਤਿਵ ਕਾਰ', 'tithai lo-a lo-a aakaar. jiv jiv hukam tivai tiv kaar.', 'There are worlds upon worlds of His Creation. As He commands, so they exist.', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 264, 'ਵੇਖੈ ਵਿਗਸੈ ਕਰਿ ਵੀਚਾਰੁ ਨਾਨਕ ਕਥਨਾ ਕਰੜਾ ਸਾਰੁ', 'vaykhai vigsai kar veechaar. naanak kathnaa karrhaa saar. 37', 'He watches, contemplates and rejoices upon His Creation O Nanak, description is extremely difficult! 37', 38);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 265, 'ਜਤੁ ਪਾਹਾਰਾ ਧੀਰਜੁ ਸੁਨਿਆਰੁ', 'jat paahaaraa Dheeraj suni-aar.', 'Let self-control be the furnace, and patience the goldsmith.', 39);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 266, 'ਅਹਰਣਿ ਮਤਿ ਵੇਦੁ ਹਥੀਆਰੁ', 'ahran mat vayd hathee-aar.', 'Let understanding be the anvil, and religious wisdom the tools.', 39);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 267, 'ਭਉ ਖਲਾ ਅਗਨਿ ਤਪ ਤਾਉ', 'bha-o khalaa agan tap taa-o.', 'Fear of God the bellows; fan the flames of your body''s inner heat.', 39);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 268, 'ਭਾਂਡਾ ਭਾਉ ਅੰਮ੍ਰਿਤੁ ਤਿਤੁ ਢਾਲਿ', 'bhaaNdaa bhaa-o amrit tit dhaal.', 'In the crucible of love, melt the Amrit Nectar of the Naam,', 39);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 269, 'ਘੜੀਐ ਸਬਦੁ ਸਚੀ ਟਕਸਾਲ', 'gharhee-ai sabad sachee taksaal.', 'Forge the Shabad in the True mint.', 39);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 270, 'ਜਿਨ ਕਉ ਨਦਰਿ ਕਰਮੁ ਤਿਨ ਕਾਰ', 'jin ka-o nadar karam tin kaar.', 'Whoever is showered with His Grace, karma of their actions excel.', 39);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 271, 'ਨਾਨਕ ਨਦਰੀ ਨਦਰਿ ਨਿਹਾਲ', 'naanak nadree nadar nihaal. 38', 'O Nanak, the Merciful Lord’s Glance uplifts and rescues them. 38', 39);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 272, 'ਸਲੋਕੁ', 'Salok.', 'Salok.', 40);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 273, 'ਪਵਣੁ ਗੁਰੂ ਪਾਣੀ ਪਿਤਾ ਮਾਤਾ ਧਰਤਿ ਮਹਤੁ', 'pavan guroo paanee pitaa maataa Dharat mahat.', 'Air is the Guru, Water is the Father, and Earth is the Great Mother of all.', 40);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 274, 'ਦਿਵਸੁ ਰਾਤਿ ਦੁਇ ਦਾਈ ਦਾਇਆ ਖੇਲੈ ਸਗਲ ਜਗਤੁ', 'divas raat du-ay daa-ee daa-i-aa khaylai sagal jagat.', 'Day and night are the two nurses, in whose lap the entire world is at play.', 40);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 275, 'ਚੰਗਿਆਈਆ ਬੁਰਿਆਈਆ ਵਾਚੈ ਧਰਮੁ ਹਦੂਰਿ', 'chang-aa-ee-aa buri-aa-ee-aa vaachai Dharam hadoor.', 'Good deeds and bad are announced in the Presence of Lord Dharma.', 40);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 276, 'ਕਰਮੀ ਆਪੋ ਆਪਣੀ ਕੇ ਨੇੜੈ ਕੇ ਦੂਰਿ', 'karmee aapo aapnee kay nayrhai kay door.', 'According to their own actions, some are drawn closer, and some are driven farther away.', 40);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 277, 'ਜਿਨੀ ਨਾਮੁ ਧਿਆਇਆ ਗਏ ਮਸਕਤਿ ਘਾਲਿ', 'jinee naam Dhi-aa-i-aa ga-ay maskat ghaal.', 'Those who have meditated on the Naam have departed after having worked by the sweat of their brows', 40);
INSERT INTO `a8471551_naam`.`paathscript` (`paathid`, `sort`, `gurmukhi`, `phonetic`, `engtranslation`, `sectionid`) VALUES (1, 278, 'ਨਾਨਕ ਤੇ ਮੁਖ ਉਜਲੇ ਕੇਤੀ ਛੁਟੀ ਨਾਲਿ', 'naanak tay mukh ujlay kaytee chhutee naal.', 'O Nanak, their faces are radiant in the Lord’s Court, and many are saved along with them!', 40);
